(() => {
  "use strict";
  window.addEventListener("DOMContentLoaded", function () {
    window.addEventListener("load", function () {
      var e, t, s, o, c, n, dropdownButton, dropdownList, tokenText;
      (e = document.querySelector(".burger")),
        (t = document.querySelector(".nav")),
        (s = document.querySelector("body")),
        (e.onclick = function () {
          e.classList.toggle("active"),
            e.classList.contains("active")
              ? (t.classList.add("active"), s.classList.add("no-scroll"))
              : (e.classList.remove("active"),
                t.classList.remove("active"),
                s.classList.remove("no-scroll"));
        }),
        window.addEventListener(
          "resize",
          function () {
            innerWidth >= 808 &&
              (e.classList.remove("active"),
              t.classList.remove("active"),
              s.classList.remove("no-scroll"));
          },
          !1
        ),
        (c = document.querySelector("#value")),
        (n = document.querySelector("#result")),
        (dropdownButton = document.getElementById("dropdownButton")),
        (dropdownList = document.getElementById("dropdownList")),
        (tokenText = document.querySelector("token__text")),
        c &&
          n &&
          c.addEventListener("input", function (e) {
            var t = +e.target.value;
            n.value = t > 0 ? 0.1 * t : "";
          }),
        dropdownButton &&
          dropdownButton.addEventListener("click", function () {
            dropdownList.style.display =
              dropdownList.style.display === "none" ||
              dropdownList.style.display === ""
                ? "block"
                : "none";
          }),
        dropdownList &&
          dropdownList.addEventListener("click", function (event) {
            if (event.target.tagName === "li") {
              tokenText.textContent = event.target.textContent;
              dropdownList.style.display = "none";
            }
          });
      (o = document.querySelector(".popup")),
        document.querySelector(".popup__close"),
        o &&
          document.addEventListener(
            "click",
            function (e) {
              e.target.classList.contains("btn--popup") &&
                (o.classList.add("popup--open"),
                o.classList.remove("popup--close")),
                (e.target.classList.contains("popup__close") ||
                  e.target.classList.contains("popup")) &&
                  (o.classList.add("popup--close"),
                  o.classList.remove("popup--open"));
            },
            !0
          );
    });
  });
})();
